import React, { useContext } from "react";
import { todoContext } from "../context/todoContext";

function Todo() {
  const { addTaskHandler, task, changeHandler, list, removeTaskHandler } =
    useContext(todoContext);

  function addTask() {
    addTaskHandler(task);
    console.log("task added : ", task);
  }

  function removeTask() {
    removeTaskHandler(task);
    console.log("task is removed : " + task);
  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            name="task"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTask()}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <>
                <div key={index}>{item}</div>
                <button onClick={() => removeTask(index)}>remove task task</button>
              </>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Todo;
