  const {
    addTaskHandler,
    task,
    changeHandler,
    visible,
    setVisible,
    list,
    removeTaskHandler,
    taskEdit,
    setTaskEdit,
    editchangeHandler,
  } = useContext(todoContext);

  function addTask() {
    addTaskHandler(task);
    console.log("task added : ", task);
  }

  function removeTask(index) {
    removeTaskHandler(index);
  }

  function editTask(item) {
    setTaskEdit(item);
    console.log("edited task list : ", list);
  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            name="task"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTask()}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <div key={index}>
                <div>{item}</div>
                <button onClick={() => removeTask(index)}>remove task</button>
