import { createContext, useEffect, useState } from "react";

export const todoContext = createContext();

export function TodoContextProvider({ children }) {
  
  const[visible,setVisible]=useState(true);
  const[taskEdit,setTaskEdit]=useState("");
  const [task,setTask]=useState("");
  const[list,setList]=useState([]);


  function addTaskHandler(task){
    setList((list)=>[...list,task]);
   
    console.log("task is : ",task)
    setTask("")
  }

  
  function removeTaskHandler(index){
    let fileterList= list.filter((_,i)=>i!== index);

    console.log("Fileter list : ",fileterList);
    setList(fileterList);
   
  }
  useEffect(()=>{
    console.log("list is : ",list)
  },[list])

  function editTaskHandler(item){
    setTaskEdit(item);
  }


  function changeHandler(singleTask){
    setTask(singleTask);
  }
  function editchangeHandler(singleTask){
    setTask(singleTask);
  }

  const value = {
    list,
    setList,
    task,
    setTask,
    visble,
    setVisible,
    taskEdit,
    setTaskEdit,
    addTaskHandler,
    removeTaskHandler,
    editTaskHandler,
    changeHandler,
    editchangeHandler
  };

  return <todoContext.Provider value={value}>{children}</todoContext.Provider>;
}
