import React, { useContext, useState } from "react";
import { todoContext } from "../context/todoContext";

function Todo() {
  const {
    addTaskHandler,
    task,
    changeHandler,
    list,
    removeTaskHandler,
    visible,
    setVisible

  } = useContext(todoContext);

  const [taskEdit, setTaskEdit] = useState("");

  function editHandler(item, index) {
    setTaskEdit(item);
    console.log()
    list[index] = item;
  }

  function addTask() {
    addTaskHandler(task);
    console.log("task added : ", task);
  }

  function removeTask(index) {
    removeTaskHandler(index);
  }

  function editTask() {
    setTaskEdit("");
    setVisible(!visible);
  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTask()}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <div key={index}>
                <div>{item}</div>
                <button onClick={() => removeTask(index)}>remove task</button>
                <button onClick={()=>setVisible(!visible)}>edit</button>
                <input
                  className={visible?"":"active"}
                  type="text"
                  value={taskEdit}
                  placeholder={item}
                  onChange={(e) => editHandler(e.target.value, index)}
                />

                {
                  visible ? (<button onClick={() => editTask(item)}>save</button>):""
                }
                
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Todo;
