import React, { useContext } from "react";
import { todoContext } from "../context/todoContext";

function Todo() {
  const { addTaskHandler, task, changeHandler,taskEdit, list, removeTaskHandler,editTaskHandler } =
    useContext(todoContext);

  function addTask() {
    addTaskHandler(task);
    console.log("task added : ", task);
  }

  function removeTask(index) {
    removeTaskHandler(index);
  }

  function editTask(item){
    editTaskHandler(item);
    console.log("item : ",item);
  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            name="task"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTask()}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <div key={index}>
                <div>{item}</div>
                <button onClick={() => removeTask(index)}>remove task</button>
                <button onClick={() => editTask(item)}>edit</button>

              </div>
            );
          })}
        </div>


        <div className="Edit-container">
            <div>
              <input
              type="text"
              value={(e)=>[...taskEdit,e]}
              onChange={changeHandler}
              />
            </div>
        </div>
      </div>
    </>
  );
}

export default Todo;
