  }

  return (
    <>
      <div>
        <div>
          <input type="text" name="task" value={task}  onChange={(e)=>changeHandler(e.target.value)} />
          <button onClick={()=>addTask()}>add task</button>
          <button onClick={()=>removeTask()}>remove task task</button>
        </div>
      </div>
    </>
  );
}

