import React, { useContext, useState } from "react";
import { todoContext } from "../context/todoContext";

function Todo() {
  const { addTaskHandler, task,setTask, changeHandler, list, removeTaskHandler,editTaskHandler } =
    useContext(todoContext);

    const[taskEdit,setTaskEdit]=useState("");

    function editHandler(note){
      setTaskEdit([...taskEdit,note]);
      console.log("edited task : ",note)
    }

  function addTask() {
    addTaskHandler(task);
    console.log("task added : ", task);
  }

  function removeTask(index) {
    removeTaskHandler(index);
  }

  function editTask(item){
    editTaskHandler(item);
    console.log("item : ",item);
  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTask()}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <div key={index}>
                <div>{item}</div>
                <button onClick={() => removeTask(index)}>remove task</button>
                <button onClick={() => editTask(item)}>edit</button>
                <input
                type="text"
                value={taskEdit}
                onChange={(e)=>editHandler(e.target.value)}
                />
              </div>
            );
          })}
        </div>


        
      </div>
    </>
  );
}

export default Todo;
