import React, { useContext, useState } from "react";
import { todoContext } from "../context/todoContext";

function Todo() {
  const {
    addTaskHandler,
    task,
    changeHandler,
    list,
    removeTaskHandler,

  } = useContext(todoContext);

  const [taskEdit, setTaskEdit] = useState("");

  function editHandler(item, index) {
    setTaskEdit(item);
    console.log(taskEdit)
    console.log(item);
    list[index] = item;

    console.log(list);
    console.log(list[index]);
  }

  function addTask() {
    addTaskHandler(task);
    console.log("task added : ", task);
  }

  function removeTask(index) {
    removeTaskHandler(index);
  }

  function editTask(item, index) {}

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTask()}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <div key={index}>
                <div>{item}</div>
                <button onClick={() => removeTask(index)}>remove task</button>
                <button>edit</button>
                <input
                  type="text"
                  value={taskEdit}
                  onChange={(e) => editHandler(e.target.value, index)}
                />
                <button onClick={() => editTask()}>save</button>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Todo;
