  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTaskHandler(task)}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <div key={index}>
                <div>{item}</div>
                <button onClick={() => removeTaskHandler(index)}>
                  remove task
                </button>
                <button onClick={() => setVisible(!visible)}>
                  {visible ? "cancel" : "edit"}
                </button>
                <input
                  className={visible ? "" : "active"}
                  type="text"
                  placeholder={item}
                  value={item}
                  onChange={(e) => editHandler(e.target.value, index)}
                />

                {visible ? (
                  <button onClick={() => editTask(item)}>save</button>
                ) : (
                  ""
                )}
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Todo;
