import React, { useContext } from "react";
import { todoContext } from "../context/todoContext";

function Todo() {
  const {
    addTaskHandler,
    task,
    changeHandler,
    visible,
    setVisible,
    list,
    removeTaskHandler,
    editTaskHandler,
  } = useContext(todoContext);

  function addTask() {
    addTaskHandler(task);
    console.log("task added : ", task);
  }

  function removeTask(index) {
    removeTaskHandler(index);
  }

  function editTask(item) {
    editTaskHandler(item);
    console.log("item : ", item);
  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            name="task"
            value={task}
            onChange={(e) => changeHandler(e.target.value)}
          />
          <button onClick={() => addTask()}>add task</button>
        </div>

        <div>
          <h1>ToDo Item</h1>

          {list.map((item, index) => {
            return (
              <div key={index}>
                <div>{item}</div>
                <button onClick={() => removeTask(index)}>remove task</button>
                <button onClick={() => setVisible(!visible)}>edit</button>

                {/* edit container */}
                <div className={visible?"active":""}>
                  <div>
                    <input
                      type="text"
                      value={item}
                      onChange={() => changeHandler()}
                    />
                  </div>

                  <button onClick={()=>editTask(item)}>save</button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Todo;
